import React, { useEffect } from "react";
import axios from "axios";
import { Modal, ModalBody, ModalHeader, ModalFooter, Button } from "reactstrap";

export default function Stats(props) {
  const { isOpen, toggle, stats } = props;
  useEffect(() => {
    // save the stats information in the database
    const fromEndpoint = window.location.href;
    axios
      .post("http://127.0.0.1:5000/saveStats", { data: stats })
      .then((resp) => {
        console.log(resp);
      });
  }, []);

  return (
    <>
      <Modal isOpen={isOpen} toggle={toggle}>
        <ModalHeader toggle={toggle}></ModalHeader>
        <ModalBody className="stat-board">
          <div>
            <div className="stat">
              <h4>{stats[0]}</h4>
            </div>
            <p>words/min</p>
          </div>
          <div>
            <div className="stat">
              <h4>{stats[1]}</h4>
            </div>
            <p>characters/min</p>
          </div>
          <div>
            <div className="stat">
              <h4>{stats[2]}</h4>
            </div>
            <p>% accuracy</p>
          </div>
        </ModalBody>
        <ModalFooter>
          <Button onClick={toggle} color="warning">
            Try Again!
          </Button>
        </ModalFooter>
      </Modal>
    </>
  );
}
